package SwitchX;

public class Swapingwithoutusing3rdvariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int temp;
		    int x = 100;
		    int y = 200;
		     
		    //Swapping in steps
		    temp = x;
		    x = y;
		    y = temp;
		     
		    //Verify swapped values
		    System.out.println("x = " + x + " and y = " + y);
	}

}
